//回文字符串判断
function isRepeat(str = ''){
    return str === str.split('').reverse().join('');
}

function longestPalindrome(str = ''){
    if(str.length <= 1){
        return str;
    }
    let maxString = '';
    for(let i = 0; i < str.length; i++){
        for(let j = i + 1; j < str.length; j++){
            if(str[i] === str[j]){
                let item = str.substring(i, j + 1);
                if(isRepeat(item) && item.length > maxString.length){
                    maxString = item;
                }
            }
        }
    }
    return maxString === '' ? str[0] : maxString;
}

